sqlMap.select:
select a.*,b.ht_date_first from (select a.* from ( select distinct a.kh_id,a.kh_name,a.provinces,a.city,a.region,a.kh_addr,a.kh_card,a.kh_phone1,a.kh_phone2,a.introduce_name from kq_kh_info a where a.is_valid =1
and a.kh_id = #kh_id#
) a ) a left join (select * from (select kh_id,ht_date_first,ROW_NUMBER() over(partition by kh_id order by ht_date_current ) rn from kq_ht_info) b where b.rn = 1) b on a.kh_id = b.kh_id

select * from (select a.*, row_number() over(order by a.kh_id) rn, count(*) over() totalCount from ( select distinct a.kh_id,a.kh_name,a.provinces,a.city,a.region,a.kh_addr,a.kh_card,a.kh_phone1,a.kh_phone2,a.introduce_name from kq_kh_info a where a.is_valid =1
and a.introduce_name = #kh_id#
) a) t

select * from (select a.*, row_number() over(order by a.ht_date_current) rn, count(*) over() totalCount from ( select distinct a.kh_addr,a.kh_id,a.kh_name,a.kh_card,a.kh_phone1,a.kh_phone2,a.introduce_name, b.ht_id,b.ht_code,b.ht_date_first,b.ht_date_current,b.ht_pledge,b.ht_rent,b.prod_name,b.prod_code,b.ht_year,b.remark ,b.ht_type from kq_kh_info a ,kq_ht_info b where a.kh_id = b.kh_id and b.is_valid =1 and a.is_valid =1
and b.ht_id = #ht_id#
) a) t

select * from (select a.*, row_number() over(order by a.ht_date_current) rn, count(*) over() totalCount from ( select distinct a.kh_addr,a.kh_id,a.kh_name,a.kh_card,a.kh_phone1,a.kh_phone2,a.introduce_name, b.ht_id,b.ht_code,b.ht_date_first,b.ht_date_current,b.ht_pledge,b.ht_rent,b.prod_name,b.prod_code,b.ht_year,b.remark ,b.ht_type from kq_kh_info a ,kq_ht_info b where a.kh_id = b.kh_id and b.is_valid =1 and a.is_valid =1
and b.kh_id = #kh_id#
) a) t

select t1.*,case when t2.hf_type=1 then '第一次回访' when t2.hf_type=2 then '第二次回访' when t2.hf_type=3 then '第三次回访' when t2.hf_type=4 then '第四次回访' else '新装' end as hf_type,t2.hf_status from (select a.kh_id,a.provinces,a.city,a.region,a.kh_name,a.kh_phone1,a.kh_addr,b.ht_id,b.ht_date_first from (select * from kq_kh_info where kh_id = #kh_id#
and charindex(a.region,#org_ids#) >0
) a left join (select * from (select t.*,ROW_NUMBER() over(partition by t.kh_id order by t.ht_id desc) rm from kq_ht_info t where t.is_valid = 1) b where b.rm = 1 ) b on a.kh_id = b.kh_id) t1 left join (select * from (select t.*,ROW_NUMBER() over(partition by ht_id order by hf_id desc) rm from kq_hf_info t where t.is_valid = 1 and t.hf_status = 1 ) b where b.rm = 1) t2 on t1.ht_id = t2.ht_id
